mapper.select:
SELECT f.`uuid`, f.`name`, f.`is_active` AS isActive, fv.`version` AS activeVersion FROM `form` f JOIN `form_version` fv ON fv.`form_uuid`=f.`uuid` AND fv.`is_active` = 1 WHERE 1=1
AND f.`is_active` = #{isActive}
AND f.`name` LIKE CONCAT('%', #{keyword},'%')
LIMIT #{startNum}, #{pageSize}

SELECT COUNT(f.`uuid`) FROM `form` f JOIN `form_version` fv ON fv.`form_uuid`=f.`uuid` AND fv.`is_active` = 1 WHERE 1=1
AND f.`is_active` = #{isActive}
AND f.`name` LIKE CONCAT('%', #{keyword},'%')

SELECT 
p.`uuid`,
p.`name` 
FROM `process` p 
JOIN `process_form` pf ON pf.`process_uuid` = p.`uuid` AND pf.`form_uuid` = #{formUuid}

LIMIT #{startNum}, #{pageSize}
